class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0;i<n;i++){
            st.insert(i);
        }
        vector<int> ans;
        for(auto it:queries){
            auto pointr=st.lower_bound(it[0]+1);
            while(pointr!=st.end() && *pointr<it[1]){
                st.erase(pointr);
                pointr=st.lower_bound(it[0]+1);
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
