class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {


        int ans;
        int sz = queries.size();
        ans = n-1;

        vector<int> v(sz);
        int i = 0;

        set<int> st;
        for(int i=0;i<n;i++){
            st.insert(i);
        }

        for(auto it: queries){

            int l = it[0], r = it[1];

            auto itr = st.upper_bound(l);
            while(itr!= st.end() && (*itr)< r){
                st.erase(itr);
                itr = st.upper_bound(l);
            }

            v[i] = st.size()-1;
            i++;

        }

        return v;
    }
};
