func shortestDistanceAfterQueries(n int, queries [][]int) []int {
  path := make([]int, n)
  for i := range path {
    path[i] = i
  }
  ans := make([]int, len(queries))
  for i, qry := range queries {
    startId := sort.SearchInts(path, qry[0])
    endId := sort.SearchInts(path, qry[1])
    if path[startId] == qry[0] && path[endId] == qry[1] {
      path = append(path[:startId+1], path[endId:]...)
    }
    ans[i] = len(path) - 1
  }
  return ans
}
