class Solution {
  public:
	vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& a) {
		// 也就是不存在交叉 。。。这个条件有什么用呢。
		// 也就是只有包含和被包含的关系。。那么也就是说。。。可以缩点？？
		// 带权并查集？？？
		// 而且实际上往回走 是没有意义的。
		//怎么看着像线段树？？？
		//
		// 用链表维护。。暴力。。前后的差就行了。。
		vector<int>ne(n + 1, n);
		for (int i = 1; i < n; ++i)
			ne[i] = i + 1;
		int sum = n - 1;
		vector<int>ans;
		for (auto &a : a) {
			int l = a[0] + 1, r = a[1] + 1;
			int cnt = 0;
			if (ne[l] >= r) {
				ans.push_back(sum);
				continue;
			}
			int p = l;
			while (1) {
				cnt++;
				int t = l;
				l = ne[l];
				ne[t] = r;
				if (l == r)
					break;
			}
			ne[p] = r;
			sum -= cnt - 1;
			ans.push_back(sum);
		}
		return ans;
	}
};
