class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> v(n,0);
        set<int> st;
        for(int i=0;i<n-1;i++){
            st.insert(i);
            v[i]=n-i-1;
        }
        st.insert(n-1);
        vector<int> ans;
        for(int i=0;i<q.size();i++){
            int x=q[i][0],y=q[i][1];
            auto it=st.upper_bound(x);
            vector<int> toErase;
            while(it != st.end() && *it < y){
                toErase.push_back(*it);
                it++;
            }
            for(int val : toErase){  
                st.erase(val);
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
