func shortestDistanceAfterQueries(n int, queries [][]int) []int {
    farthestEdge := make(map[int]int, n)
	dist := n - 1
	output := make([]int, len(queries))
	for i := range n - 1 {
		farthestEdge[i] = i + 1
	}
	// farthestEdge == 0 <=> this node is nit on current shortest path

	for i, query := range queries {
		farthestEdgeSoFar := farthestEdge[query[0]]
		if farthestEdgeSoFar == 0 || query[1] <= farthestEdgeSoFar {
            output[i] = dist
			continue
		}

		step := 1
		cur := farthestEdge[query[0]]
		for cur != query[1] {
			step += 1
			next := farthestEdge[cur]
			farthestEdge[cur] = 0
			cur = next
		}

		dist -= step - 1
        farthestEdge[query[0]] = query[1]

		output[i] = dist
	}
	return output
}
