#define ll long long
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {

        int m=q.size();
        vector<int>dis(n,-1);
        dis[n-1]=n+1;
        ll p=n-1;
        vector<int>ans(m,0);
        for(int i=0;i<m;i++){
            int u=q[i][0];
            int v=q[i][1];
            ll dist=0;

            for(int k=u;k<=v;k++){

                if(dis[k]!=-1){
                    dist++;
                    int temp=dis[k];
                    dis[k]=max(dis[k],v);
                    k=max(temp-1,k);

                }
                else{
                    dist++;
                    dis[k]=max(dis[k],v);

                }
            }

            dist-=2;
            dist=max(dist,0LL);

            ans[i]=p-dist;
            p-=dist;

        }
        return ans;
    }
};
