class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeMap<Integer, Integer> m = new TreeMap<>();
        int[] dis = new int[queries.length];
        int idx = 0;

        for(int i=0; i<n-1; i++) {
            m.put(i, i+1);
        }

        for(int[] q: queries) {
            var L = m.floorEntry(q[0]);
            if(L != null && L.getValue() >= q[1]) {
                dis[idx++] = m.size();
            } else {
                m.subMap(q[0], q[1]).clear();
                m.put(q[0], q[1]);
                dis[idx++] = m.size();
            }
        }

        return dis;
    }
}
