class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int,int>>sx;
        set<int>end;
        for(int i=0;i<n-1;i++)
        sx.insert({i,i+1});
        map<int,int>end_map;
        vector<int>ans;
        for(int i = 0;i<n-1;i++)
        {
            end.insert(i+1);
            end_map[i+1] = i;
        }
        for(int i = 0;i<queries.size();i++)
        {
            int x = queries[i][0];
            int y = queries[i][1];
            if(end_map[y]!=-1)
            {
                if(end_map[y]>x)
                {
                    auto it = end.find(y);
                    vector<int>vx;
                    while(end_map[(*it)]>=x)
                    {
                        int ye = (*it);
                        vx.push_back(ye);
                        int xe = end_map[ye];
                        sx.erase({xe,ye});
                        end_map[ye] = -1;
                        if(it!=end.begin())
                        it--;
                        else
                        break;
                    }
                    cout<<endl;
                    end_map[y] = x;
                    for(auto a:vx)
                    end.erase(a);
                    end.insert(y);
                    sx.insert({x,y});
                }
            }
            ans.push_back(sx.size());

        }
        return ans;
    }
};
