class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int> > s;
        for (int i = 0; i < n - 1; i++) {
            pair<int, int> p{i, i + 1};
            s.insert(p);
        }

        vector<int> ret;
        int ans = n - 1;
        for (auto &q : queries) {
            auto lb = s.lower_bound({q[0], -1});
            if (lb != s.end() && lb->first < q[1]) {
                vector<pair<int, int> > del;
                while (lb != s.end() && lb->first < q[1]) {
                    ans -= 1;
                    del.push_back({lb->first, lb->second});
                    lb++;
                }
                for (auto &p : del) {
                    s.erase(p);
                }
                pair<int, int> p{q[0], q[1]};
                s.insert(p);
                ans += 1;   
            }
            ret.push_back(ans);
        }

        return ret;
    }
};
