class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        unordered_map<int, int> mp;
        for(int i = 0; i < n - 1; i++)
        {
            mp[i] = i + 1;
            s.insert(i);
        }
        vector<int> ans;
        int q = queries.size();
        for(int i = 0; i < q; i++)
        {
            int l = queries[i][0];
            int r = queries[i][1];
            auto pos = s.lower_bound(l);
            while(pos != s.end())
            {
                if(mp[*pos] > r)
                    break;
                pos = s.erase(pos);
            }
            auto p = s.upper_bound(l);
            if(p == s.begin())
            {
                mp[l] = r;
                s.insert(l);
            }
            else
            {
                if(p == s.end())
                {
                    p = prev(s.end());
                }
                else
                    p--;
                if(mp[*p] < r)
                {
                    mp[l] = r;
                    s.insert(l);
                }   
            }
            ans.push_back(s.size());
        }
        return ans;
    }
};
