class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n,1);
        vector<int> vec(n,0);
        for(int i=0;i<n-1;i++){
            vec[i]=i+1;
        }
        int cnt=n-1;
        vector<int> ans;

        for(int i=0;i<queries.size();i++){
            int u=queries[i][0];
            int v=queries[i][1];
            if(dist[u]){
                for(int j=vec[u];j<v;j++){
                    if(dist[j]){
                        cnt--;
                        dist[j]=0;
                    }
                }
                vec[u]=v;
            }
            // for(int j=0;j<dist.size();j++){
            //     cout<<dist[j]<<" ";
            // }
            // cout<<"\n";
            ans.push_back(cnt);
        }
        return ans;
    }
};
