#include <bits/stdc++.h>
#include <bits/extc++.h>
using namespace std;
using namespace __gnu_pbds;

template<class T> using oset=tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>;


class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {

        oset<int> path; for (int i = 0; i < n; ++i) path.insert(i);


        auto del_in_range = [&](int l, int r) {
            while (1) {
                auto it = path.lower_bound(l);
                if (it == path.end()) break;
                if (*it > r) break;
                path.erase(it);
            }
        };

        vector<int> ANS;
        for (auto V : q) {
            int u = V[0], v = V[1];

            del_in_range(u+1, v-1);
            ANS.push_back(path.size()-1);
        }
        return ANS;
    }
};
