class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        intervals = [[i,i+1] for i in range(n-1)]
        result = []
        for a,b in queries:
            i = bisect_left(intervals,[a,0])
            if i < len(intervals) and intervals[i][0] == a:
                intervals[i][1] = b
                while i+1 < len(intervals) and intervals[i+1][1] <= b:
                    intervals.pop(i+1)
            result.append(len(intervals))
        return result
