class Solution {
public:    
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> ans;
        set<int> s;

        for(int i = 0; i < n; i++) 
            s.insert(i);

        for(auto e : q) {
            int u = e[0], v = e[1];
            auto itr = s.lower_bound(u+1);
            int i = 10;
            while((*itr) < v) {
                s.erase(itr);
                itr = s.lower_bound(u+1);
            }

            ans.push_back(s.size()-1);
        }

        return ans;
    }
};
