class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int, int> mp;
        set<int> se;
        vector<int> ans;
        int cur = n - 1;
        for (auto &q : queries) {
            auto it = se.lower_bound(q[0]);
            if (it != se.begin()) {
                auto it0 = prev(it);
                if (mp[*it0] >= q[1]) {
                    ans.push_back(cur);
                    continue;
                }
            }
            if (*it == q[0] && mp[*it] > q[1]) {
                ans.push_back(cur);
                continue;
            }
            vector<set<int>::iterator> rem;
            while (it != se.end() && mp[*it] <= q[1]) {
                rem.push_back(it);
                cur += mp[*it] - *it - 1;
                it++;
            }
            cur -= q[1] - q[0] - 1;
            mp[q[0]] = q[1];
            for (auto &x : rem) {
                se.erase(x);
            }
            se.insert(q[0]);
            ans.push_back(cur);
        }
        return ans;
    }
};
