class Solution {
public:


vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    vector<int> path(n);
    set<int> st;
    for (int i = 0; i < n; i++)
        st.insert(i);

    for (int i = 0; i < n - 1; i++)
    {
        path[i] = i + 1;
    }

    vector<int> res;
    int nres = n - 1;
    for (auto& q : queries) {
        if (q[1] < q[0])
        {
            res.push_back(nres);;
            continue;
        }

        auto iter = st.find(q[0]);
        if (iter == st.end())
        {
            res.push_back(nres);;
            continue;
        }

        iter++;
        while (iter != st.end()) {
            if (*iter >= q[1])
                break;
            iter = st.erase(iter);
            nres--;
        }
        res.push_back(nres);
    }
    return res;
}
};
