class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<pair<int, int>> st;
        for (int i = 0; i < n - 1; ++i) {
            st.insert({i, i + 1});
        }
        for (const auto& query : queries) {
            int u = query[0], v = query[1];
            auto it1 = st.lower_bound({u + 1, u + 2});
            auto it2 = st.lower_bound({v, v + 1});
            while (it1 != it2) {
                it1 = st.erase(it1);
            }
            ans.push_back(st.size());
        }

        return ans;
    }
};
