class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> mp;
        vector<int> res;
        for (int i = 1; i < n; ++i) mp[i - 1] = i;
        for (auto& q : queries) {
            auto iter = mp.lower_bound(q[0]);
            if (iter == mp.end() || iter->first != q[0]) {
                // cout << iter->second << endl;
            } else if (iter->second < q[1]) {
                while (iter != mp.end() && iter->second <= q[1]) iter = mp.erase(iter);

                mp[q[0]] = q[1];
                // for (auto [k,v]: mp) cout << k << " " << v << endl;
                // cout << endl;
            }
            res.push_back(mp.size());
        }
        return res;
    }
};
