class Solution {
    fun shortestDistanceAfterQueries(n: Int, queries: Array<IntArray>): IntArray {
        var m1 = TreeSet<Int>()
        for (i in 0 until n) m1.add(i)
        var epiv = mutableListOf<Int>(n - 1)
        for ((l, r) in queries) {
            if (!m1.contains(l) || !m1.contains(r)) { epiv.add(epiv.last());continue }
            while (true) {
                var ans = m1.higher(l) ?: break
                if (ans >= r) break;m1.remove(ans)
            }
            epiv.add(m1.size - 1)
        }
        return epiv.slice(1 until epiv.size).toIntArray()
    }
}
