    class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>res ;
        int r = n - 1 ;
        vector< int > next ( n ) ;
        rep( i , n ) next[i]= i+ 1;

        for( auto i : queries )
        {
        	int u = i [0] , v = i[1] ;

        	int a = u , c = 0 ;

            if( next[u] > v ) {
                res.pb(r) ; continue ;
            }

            // print( next ) ;

        	 while( next[u] <= v )
        	 {
        	 	c ++ ; 
                 int x = u ; u = next[u] ; next[x]  = n+2 ;
        	 }
        	 next[a] =v  ;
            // pp(c,u) ;
            // print( next )

        	 r -=  c-1 ;
        	res.pb( r ) ;
        }
        return res ;
    }
};
