class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n);
        set<int> st;
        vector<int> ans;
        for(int i=0;i<n;i++) st.insert(i);
        for(int i=0;i<queries.size();i++) {
            auto it=st.lower_bound(queries[i][0]);
            if(it==st.end()) ans.push_back(st.size()-1);
            else {
                it++;
                while(it!=st.end()) {
                    if(*it>=queries[i][1]) break;
                    it=st.erase(it);
                }
                ans.push_back(st.size()-1);
            }
        }
        return ans;
    }
};
