class Solution {
public:
    class Node {
    public:
      int data;
      Node* next;
      Node(int data){
        this->data = data;
        this->next = NULL;
      }
    };

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int ans=n-1;
        map<int,Node*> m;
        Node* head= new Node(0);
      m[0]=head;
      Node* tail; tail=head;
        for(int i=1;i<n;i++){
          Node* temp = new Node(i);
          m[i]=temp;
          tail->next=temp;
          tail=temp;
        }

      vector<int> v;

      for(int i=0;i<queries.size();i++){
          int a=queries[i][0];
          int b=queries[i][1];

          if(m.find(a)==m.end() || m.find(b)==m.end()){v.push_back(ans);continue;}
        else{
          Node* temp= m[a];
          temp=temp->next;
          Node* head=m[a];
          while(temp && temp->data!=b){
            m.erase(temp->data);
            ans--;
            temp=temp->next;
          }
          head->next=temp;
          v.push_back(ans);
        }
      }

      return v;


    }
};
