class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> sp;
        for(int i = 0; i < n; ++i) sp.insert(i);
        int m = queries.size();
        vector<int> res(m, 0);
        for(int i = 0; i < m; ++i){
            int x = queries[i][0], y = queries[i][1];
            if(x > y) swap(x, y);
            auto l = sp.upper_bound(x), r = sp.lower_bound(y);
            vector<int> to_erase;
            for(auto it = l; it != r; ++it){
                to_erase.push_back(*it);
            }
            for(auto c : to_erase) sp.erase(sp.find(c));
            res[i] = sp.size() - 1;
        }
        return res;
    }
};
