class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        set<int> s;

        for(int i=0;i<n;i++)
        {
            s.insert(i);
        }

        vector<int> res;

        for(int i=0;i<queries.size();i++)
        {
            auto it= s.upper_bound(queries[i][0]);    
            vector<int> todel;
            while(it!=s.end() && *it<queries[i][1])
            {
                todel.push_back(*it);
                it++;
            }
            for(auto d: todel)
            {
                s.erase(d);
            }
            res.push_back(s.size()-1);
        }
        return res;
    }
};
