class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> result; result.reserve(n);
        for (auto i : queries) {
            int x = i[0];
            int y = i[1];
            auto t1 = s.lower_bound(x); 
            auto t2 = s.lower_bound(y);
            if (*t1==x&&*t2==y) {
                t1++;
                while (t1 != t2)
                {
                    auto j = t1;
                    t1++;
                    s.erase(*j);
                }
            }
            result.push_back(s.size() - 1);
        }
        return result;
    }
};
