class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>s;
         map<int,int>m;
        for(int i=0;i<n;i++){
            s.insert(i);
            m[i]=i+1;
        }



        vector<int>ans;

        for(auto i:queries){
           if( s.find(i[0])==s.end()){ans.push_back(s.size()-1);continue;}

           if(m[i[0]]<i[1]){
               int j=m[i[0]];
               m[i[0]]=i[1];

               while(j!=i[1]){
               s.erase(j);
                   j=m[j];
               }
           }

            ans.push_back(s.size()-1);
        }

        return ans;
    }
};
