class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> Set;
		vector<int> Result;
		int M = queries.size();
		int i;

		for(i = 0; i < n; i++)
		{
			Set.insert(i);
		}

		for(i = 0; i < M; i++)
		{
			vector<int> Temp;

			for(auto it = Set.lower_bound(queries[i][0] + 1); it != Set.end() && *it < queries[i][1]; ++it)
			{
				Temp.push_back(*it);
				//cout << i << " " << *it << endl;
			}

			for(auto it : Temp)
			{
				Set.erase(it);
			}

			Result.push_back(Set.size() - 1);
		}

		return Result;
    }
};
