from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        sl = SortedList()
        for i in range(n):
            sl.add(i)
        for u, v in queries:
            if u >= v:
                ans.append(len(sl)-1)
            else:
                x = sl.bisect_left(u)
                y = sl.bisect_left(v)
                for i in range(y-1, x, -1):
                    sl.remove(sl[i])
                ans.append(len(sl)-1)
        return ans
