class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> set = new TreeSet<>();
        for (int i = 0; i < n; i++) {
            set.add(i);
        }
        int m = queries.length;
        int[] res = new int[m];
        for (int i = 0; i < m; i++) {
            int[] q = queries[i];
            Integer left = set.higher(q[0]);
            while (left != null && left < q[1]) {
                Integer next = set.higher(left);
                set.remove(left);
                left = next;
            }
            res[i] = set.size() - 1;
        }
        return res;
    }
}
