class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int[] ans = new int[queries.length];
        TreeSet<Integer> set = new TreeSet<>();

        for (int i = 0; i < n; i++) {
            set.add(i);
        }
        int i=0;
        for (int[] query : queries) {
            int from = query[0], to = query[1];
            while (set.higher(from) != null && set.higher(from) < to) {
                int temp = set.higher(from);
                set.remove(temp);
                from = temp;
            }
            ans[i]=set.size() - 1;
            i++;
        }

        return ans;

    }
}
