function shortestDistanceAfterQueries(n: number, queries: number[][]): number[] {

    const ans = [];
    let res = n - 1;
    const flag = [];
    const ne = [];

    for(let i = 0 ; i < n ; i ++ ) {
        flag[i] = false;
        ne[i] = i + 1;
    }

    let m = queries.length;

    for(let i = 0 ; i < m ; i ++ ) {
        const [x,y] = queries[i];
        if(!flag[x] && !flag[y]) {
            let count = 1;
            for(let j = ne[x] ; j != y && j < n ;j = ne[j]) {
                flag[j] = true;
                count ++ ;
            }
            ne[x] = y;
            res -= count - 1;
        }
        ans[i] = res;
    }


    return ans;
};
