class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> A;
        for(int i=0;i<n;i++)
        {
            A.insert(i);
        }
        for(int i=0;i<queries.size();i++)
        {
            if(A.find(queries[i][0])!=A.end())
            {
                map<int,int> C;
                auto it=A.lower_bound(queries[i][0]);
                it++;
                while(it!=A.end() && *it<queries[i][1]) {
                    C[*it]++;
                    it++;
                }
                for(auto it1:C)
                {
                    A.erase(it1.first);
                }
            }
            ans.push_back(A.size()-1);
        }
        return ans;
    }
};
