class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> nodes;
        for(int i = 0; i < n; i++)
            nodes.insert(i);

        vector<int> ans;
        for(int i = 0; i < queries.size(); i++) {
            int s = queries[i][0];
            int t = queries[i][1];
            set<int>::iterator start = nodes.find(s);
            set<int>::iterator end = nodes.find(t);
            if(start != nodes.end() && end != nodes.end()) {
                start++;
                if(start != end)
                    nodes.erase(start, end);
            }
            ans.push_back(nodes.size() - 1);
        }

        return ans;        
    }
};
