    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0;i<n;i++){
            st.insert(i);
        }
        vector<int> ans;
        for(auto x: queries){
            int k=x[0]+1;
            vector<int>del;
            auto ptr=st.lower_bound(k);
            k=*ptr;
            while(k<x[1] && k<n&&ptr!=st.end()){
                del.push_back(k);
                ptr++;
                k=*ptr;
            }
            for(auto it:del){st.erase(it);}
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
