int ne[110000];

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int i,j,k,x,y,xx,tmp,tot,o;
        vector<int> ans;
        for (i=0;i+1<n;i++)
            ne[i]=i+1;
        ne[n-1]=n-1;
        ans.clear();
        tot=n-1;
        for (o=0;o<queries.size();o++)
        {
            x=queries[o][0];
            y=queries[o][1];
            if ((ne[x]!=-1)&&(ne[y]!=-1))
            {
                xx=x;
                while (xx!=y)
                {
                    tmp=ne[xx];
                    ne[xx]=-1;
                    xx=tmp;
                    tot--;
                }
                ne[x]=y;
                tot++;
            }
            ans.push_back(tot);
        }
        return ans;
    }
};
