class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        arr = []
        ret = []
        for i in range(n-1):
            arr.append((i, i+1))
        for x, y in queries:
            if x < y:
                i = bisect_left(arr, (x, 0))
                j = bisect_left(arr, (y, 0))
                if i < j:
                    del arr[i:j]
                    insort(arr, (x, y))
            ret.append(len(arr))
        return ret
