class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ret;
        vector<bool> flag(n, true);

        vector<int> graph(n);
        for(int i=0; i<n-1; i++)
            graph[i] = i+1;

        int len = n-1;
        for(auto querie : queries) {
            int u = querie[0], v = querie[1];
            if(flag[u] && v > graph[u]) {
                int num = 0;
                int cur = graph[u];
                while(cur != v) {
                    flag[cur] = false;
                    cur = graph[cur];
                    num++;
                }
                graph[u] = v;
                len -= num;
            }
            ret.push_back(len);
        }
        return ret;
    }
};
