class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> st;
        for(int i = 0; i < n; i ++) st.insert(i);
        for(auto x: queries)
        {
            int l = x[0], r = x[1];
            auto itl = st.upper_bound(x[0]);
            auto itr = st.lower_bound(x[1]);
            //cout << *itl << endl;
            auto ittp = itl;
            while(*itl < r)
            {
                ittp = itl;
                ittp ++;
                st.erase(itl);
                itl = ittp;
            }
            //cout << "--" << *ittp << " --" << endl;
            //for(auto c : st) cout << c << " ";
            //cout << endl;
            ans.push_back(st.size() - 1);
        }
        return ans;
    }
};
