class Solution {
	private static class City {
		boolean hidden;
		City next;

		City(City next) {
			this.next = next;
		}

		City hide() {
			hidden = true;
			return next;
		}
	}

	public static int[] shortestDistanceAfterQueries(int n, int[][] queries) {
		City[] cities = new City[n];
		City firstCity = null;
		for (int i = n - 1; i >= 0; i--)
			cities[i] = firstCity = new City(firstCity);
		int distance = n - 1;
		int m = queries.length;
		int[] answer = new int[m];
		for (int i = 0; i < m; i++) {
			int[] query = queries[i];
			City u = cities[query[0]];
			City v = cities[query[1]];
			if (!(u.hidden || v.hidden)) {
				City city = u.next;
				while (city != v) {
					distance--;
					city = city.hide();
				}
				u.next = v;
			}
			answer[i] = distance;
		}
		return answer;
	}
}
