from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList()
        d = defaultdict(int)
        skips = 0
        output = []
        for a, b in queries:
            x = sl.bisect_left((a - 0.1, 0))
            if d[a] >= b:
                pass
            elif x - 1 >= 0 and sl[x - 1][1] >= b:
                pass
            else:
                d[a] = b
                removal = []
                for i in range(sl.bisect_left((a, -1)), sl.bisect_left((b, -1))):
                    x, y = sl[i]
                    skips -= (y - x - 1)
                    removal.append(sl[i])
                for r in removal:
                    sl.remove(r)
                sl.add((a, b))
                skips += (b - a - 1)
            output.append(n - 1 - skips)
        return output
