class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>s;
        vector<int>ans;
        for(int i=0;i<n;i++)s.insert(i);
        for(int i=0;i<queries.size();i++){
            int x=queries[i][0];
            int y=queries[i][1];
          int  val=x+1;
            while(val<y){
                auto it=s.lower_bound(val);
                int k=(*it);
                if((*it)>=y)break;
                 s.erase(it);
                 auto itt=s.upper_bound(k);
                 if(itt==s.end())break;
                val=*itt;
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
