class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<vector<int>> adj(n,vector<int>());
        vector<int> ans;
        int count = n-1;
        for(int i=0;i<n-1;i++)adj[i].push_back(i+1);

        for(int i=0;i<queries.size();i++){            
            int l = queries[i][0];
            if(adj[l].size()==0){
                //same ans
            }else{
                if(queries[i][1]>adj[l][0]){

                    int j=l,time=0;
                    while(adj[j].size()!=0 && adj[j][0]<=queries[i][1]){
                        // cout<<"entry"<<j<<" "<<adj[j][0]<<endl;
                        time++;
                        int k=adj[j][0];
                        adj[j].clear();
                        j=k;
                    }
                    adj[l].push_back(queries[i][1]);
                    count-=(time-1);
                }else{
                    //same ans
                }
            }
            // for(int i=0;i<n-1;i++)if(adj[i].size()>0)cout<<adj[i][0]<<","<<i<<"    ";
            // cout<<endl;


            ans.push_back(count);
        }
        return ans;
    }
};
