class Solution {
public:

    static const int N=100000+10;
    int p[N], next[N], val[N];

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        for (int i=0;i<n;i++) {
            p[i]=i;
            next[i]=i+1;
            val[i]=i;
        }

        vector <int> ret;
        int ans=n-1;

        for (int jj=0;jj<queries.size();jj++) {
            int x=queries[jj][0];
            int y=queries[jj][1];

            if (p[x]==p[y]) {
                ret.push_back(ans);
                continue;
            }

            int j=p[x];
            while (true) {

                j=next[j];
                if (j>=n || val[j]>=y) break;

                p[val[j]]=p[x];
                ans--;
            }

            next[p[x]]=j;
            val[p[x]]=y-1;

            ret.push_back(ans);
        }

        return ret;
    }
};
