class Solution {
    class Node {
        Node next;
        int value;
        Node(int value) {
            this.value = value;
        }
        public String toString() {
            return "[value= " + value + "]";
        }
    }
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        Node dummy = new Node(-1);
        Node node = dummy;
        Map<Integer, Node> map = new HashMap<>();
        for(int i = 0; i < n; i++) {
            node.next = new Node(i);
            node = node.next;
            map.put(i, node);
        }
        // System.out.println("map: " + map);
        int len = queries.length;
        int[] res = new int[len];
        for(int i = 0; i < len; i++) {
            // System.out.println("-------");
            int[] query = queries[i];
            int from = query[0], to = query[1];
            // System.out.println("from: " + from + ", to: " + to);
            // System.out.println("before");
            // System.out.println("map: " + map);
            // helper(dummy);
            if(map.containsKey(from)) {
                Node cur = map.get(from);
                Node prev = null;
                while(cur != null && cur.value < to) {
                    if(prev != null) {
                        if(prev.next != null) map.remove(prev.next.value);
                        prev.next = null;
                    }
                    prev = cur;
                    cur = cur.next;
                }
                if(cur != null && cur.value == to) map.get(from).next = cur;
            }
            res[i] = map.size() - 1;
            // System.out.println("after");
            // System.out.println("map: " + map);
            // helper(dummy);
        }
        return res;
    }

    void helper(Node node) {
        Node cur = node;
        while(cur != null) {
            System.out.println(cur);
            cur = cur.next;
        }
    }
}
