class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> intervals;
        for (int i = 0; i < n - 1; i++) {
            intervals[i] = i+1;
        }

        vector<int> ans;
        int curMin = n - 1;

        for (vector<int> q: queries) {
            int l = q[0], r = q[1];

            auto lit = intervals.lower_bound(l);
            auto rit = intervals.lower_bound(r);
            rit--;

            if (lit == intervals.end() || lit->second >= r) {
                ans.push_back(intervals.size());
            } else {
                intervals.erase(lit, ++rit);
                intervals[l] = r;
                ans.push_back(intervals.size());
            }
        }
        return ans;
    }
};
