class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> s;
        for(int i =0; i<n; i++){
            s.insert(i);
        }

        for(auto x : queries){
            int from = x[0], to = x[1];
            while(s.upper_bound(from) != s.end() && *s.upper_bound(from) < to){
                int temp = *s.upper_bound(from);
                s.erase(temp);
                from = temp;
            }
            ans.push_back(s.size() - 1);
        }

        return ans;
    }
};
