class Solution {
    fun shortestDistanceAfterQueries(n: Int, queries: Array<IntArray>): IntArray {
       val adjList = Array(n){if(it==n-1) mutableListOf<Int>() else mutableListOf<Int>(it+1)} 
       val ans =IntArray(queries.size)
       val set= TreeSet<Int>()
       for(i in 0..n-1){
            set.add(i)
       }
       for((i,v) in queries.withIndex()){
            if(v[0] in set && v[1] in set){
                val list=mutableListOf<Int>()
                val iter = set.tailSet(v[0],false)
                for(value in iter){
                    if(value==v[1]){
                        break
                    }else{
                        list.add(value)
                    }
                }
                for(value in list){
                    set.remove(value)
                }
            } 
            ans[i]=set.size-1
       }

       return ans
    }
}
