class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<vector<int>> g(n);
        for(int i = 0; i < n-1; i++)
        {
            g[i].push_back(i+1);
        }
        int q = queries.size();
        vector<int> ans(q, -1);
        int st = n-1;
        unordered_set <int> vis;
        unordered_map<int,int> mp;
        for(int i = 0; i < q; i++)
        {
            if(vis.find(queries[i][0]) != vis.end())
            {
                if(mp.find(queries[i][0]) != mp.end() && mp[queries[i][0]] < queries[i][1])
                {
                    st -= (- mp[queries[i][0]] + queries[i][1]);
                    for(int j = mp[queries[i][0]]; j < queries[i][1]; j++)
                    {
                        if(mp.find(j) != mp.end())
                        {
                            st += (mp[j] - j - 1);
                            mp.erase(j);
                        }
                        vis.insert(j);
                    }
                    mp[queries[i][0]] = queries[i][1];
                }
            }
            else
            {
                st -= (queries[i][1] - queries[i][0] - 1);
                for(int j = queries[i][0]; j < queries[i][1]; j++)
                {
                    if(mp.find(j) != mp.end())
                    {
                        st += (mp[j] - j - 1);
                        mp.erase(j);
                    }
                    vis.insert(j);
                }
                mp[queries[i][0]] = queries[i][1];
            }
            ans[i] = st;
        }
        return ans;
    }
};
