set<int> st;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
      st.clear();
      for (int i = 0; i < n; ++i) {
        st.insert(i);
      }
      int m = queries.size();
      vector<int> ret(m);
      for (int i = 0; i < m; ++i) {
        int u = queries[i][0], v = queries[i][1];
        for (auto it = st.upper_bound(u); it != st.end() && *it < v; ) {
          it = st.erase(it);
        }
        ret[i] = st.size() - 1;
      }
      return ret;
    }
};
